export const obj = {
    parseXML: function (data) {
        var xml, tmp;
        if (!data || typeof data !== "string") {
            return null;
        }
        try {
            if (window.DOMParser) {
                // Standard
                tmp = new DOMParser();
                xml = tmp.parseFromString(data, "text/xml");
            } else {
                // IE
                xml = new ActiveXObject("Microsoft.XMLDOM");
                xml.async = "false";
                xml.loadXML(data);
            }
        } catch (e) {
            xml = undefined;
        }
        if (
            !xml ||
            !xml.documentElement ||
            xml.getElementsByTagName("parsererror").length
        ) {
            jQuery.error("Invalid XML: " + data);
        }
        return xml;
    },
};
