children.data:
stop.begin { stop-color:yellow; }
stop.end { stop-color:green; }
body.invalid stop.end { stop-color:red; }
#err { display:none; }
body.invalid #err { display:inline; }

children.raw:
stop.begin { stop-color:yellow; }
stop.end { stop-color:green; }
body.invalid stop.end { stop-color:red; }
#err { display:none; }
body.invalid #err { display:inline; }

children.data:
div {
    color: white;
    font: 18px serif;
    height: 100%;
    overflow: auto;
}

children.raw:
div {
    color: white;
    font: 18px serif;
    height: 100%;
    overflow: auto;
}
